const Captcha = ({ onValid }) => {
  const [randomNr1, setRandomNr1] = useState(0);
  const [randomNr2, setRandomNr2] = useState(0);
  const [userInput, setUserInput] = useState('');
  const [isValid, setIsValid] = useState(false);

  useEffect(() => {
    // Generate random numbers when component mounts
    const nr1 = Math.floor(Math.random() * 10);
    const nr2 = Math.floor(Math.random() * 10);
    setRandomNr1(nr1);
    setRandomNr2(nr2);
  }, []);

  useEffect(() => {
    // Check if the user's input matches the sum of the two random numbers
    const totalNr = randomNr1 + randomNr2;
    setIsValid(parseInt(userInput, 10) === totalNr);
  }, [userInput, randomNr1, randomNr2]);

  const handleChange = (e) => {
    setUserInput(e.target.value);
  };

  return (
    <div>
      <label htmlFor="captchaInput">What is {randomNr1} + {randomNr2}?</label>
      <input
        type="number"
        id="captchaInput"
        value={userInput}
        onChange={handleChange}
      />
    </div>
  );
};

const Form = () => {
  const [isCaptchaValid, setIsCaptchaValid] = useState(false);

  const handleCaptchaValid = (valid) => {
    setIsCaptchaValid(valid);
  };

  return (
    <form onSubmit={(e) => { if (!isCaptchaValid) e.preventDefault(); }}>
      <Captcha onValid={handleCaptchaValid} />
      <button type="submit" disabled={!isCaptchaValid}>
        Submit
      </button>
    </form>
  );
};

export default Form;
