import React, { useState } from 'react';
import 'App.css'; // Import CSS file

function Counter() {
  const [like, setLike] = useState(0);
  const [angry, setAngry] = useState(0);
  const [sad, setSad] = useState(0);
  const [haha, setHaha] = useState(0);
  const [liked, setLiked] = useState(false);
  const [angered, setAngered] = useState(false);
  const [sadded, setSadded] = useState(false);
  const [hahaed, setHahaed] = useState(false);

  const handleLikeClick = () => {
    if (liked) {
      setLike(like - 1);
    } else {
      setLike(like + 1);
    }
    setLiked(!liked);
  };

  const handleAngryClick = () => {
    if (angered) {
      setAngry(angry - 1);
    } else {
      setAngry(angry + 1);
    }
    setAngered(!angered);
  };

  const handleSadClick = () => {
    if (sadded) {
      setSad(sad - 1);
    } else {
      setSad(sad + 1);
    }
    setSadded(!sadded);
  };

  const handleHahaClick = () => {
    if (hahaed) {
      setHaha(haha - 1);
    } else {
      setHaha(haha + 1);
    }
    setHahaed(!hahaed);
  };

  return (
    <div className="counter">
      <div className="buttons">
        <button className="like-btn" onClick={handleLikeClick}>
          {liked ? 'Unlike' : 'üëç'}
        </button>
        <button className="angry-btn" onClick={handleAngryClick}>
          {angered ? 'Unangry' : 'üò°'}
        </button>
        <button className="sad-btn" onClick={handleSadClick}>
          {sadded ? 'Unsad' : 'üò¢'}
        </button>
        <button className="haha-btn" onClick={handleHahaClick}>
          {hahaed ? 'Unhaha' : 'üòÇ'}
        </button>
      </div>
      <div className="reaction">
        <h1 className="like-count">üëç {like}</h1>
        <h1 className="angry-count">üò° {angry}</h1>
        <h1 className="sad-count">üò¢ {sad}</h1>
        <h1 className="haha-count">üòÇ {haha}</h1>
      </div>
    </div>
  );
}

export default Counter;
