// import React from 'react';

const Toolbar = ({ setBrushColor, setBrushRadius }) => {
  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'center', margin: '10px' }}>
        <span>Brush size: </span>
        <button onClick={() => setBrushRadius(8)} style={{ backgroundColor: '#000', width: '30px', height: '30px', borderRadius: '50%', margin: '5px' }}></button>
        <button onClick={() => setBrushRadius(16)} style={{ backgroundColor: '#000', width: '40px', height: '40px', borderRadius: '50%', margin: '5px' }}></button>
        <button onClick={() => setBrushRadius(24)} style={{ backgroundColor: '#000', width: '50px', height: '50px', borderRadius: '50%', margin: '5px' }}></button>
      </div>
      <div style={{ display: 'flex', justifyContent: 'center', margin: '10px' }}>
        <span>Color: </span>
        <button onClick={() => setBrushColor("#FF0000")} style={{ backgroundColor: '#FF0000', width: '30px', height: '30px', margin: '5px' }}></button>
        <button onClick={() => setBrushColor("#8A2BE2")} style={{ backgroundColor: '#8A2BE2', width: '30px', height: '30px', margin: '5px' }}></button>
        <button onClick={() => setBrushColor("#FFB6C1")} style={{ backgroundColor: '#FFB6C1', width: '30px', height: '30px', margin: '5px' }}></button>
      </div>
    </div>
  );
};

function App() {

  return (
    <div className="App">
      <Toolbar setBrushColor={setBrushColor} setBrushRadius={setBrushRadius} />
      {/* <DrawingBoard brushColor={brushColor} brushRadius={brushRadius} /> */}
    </div>
  );
}

export default App;
