import React, { useEffect, useState } from 'react';
// import axios from 'axios';

const App = () => {
  const [num1, setNum1] = useState(0);
  const [num2, setNum2] = useState(0);
  const [answer, setAnswer] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchCaptcha();
  }, []);

  const fetchCaptcha = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/captcha');
      setNum1(response.data.num1);
      setNum2(response.data.num2);
    } catch (error) {
      console.error('Error fetching captcha', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/validate', { answer: parseInt(answer) });
      if (response.data.success) {
        setMessage('Correct!');
      } else {
        setMessage('Incorrect, try again.');
      }
    } catch (error) {
      console.error('Error validating captcha', error);
    }
  };

  return (
    <div>
      <h1>Captcha Tính Tổng</h1>
      <p>{num1} + {num2} = ?</p>
      <form onSubmit={handleSubmit}>
        <input type="number" value={answer} onChange={(e) => setAnswer(e.target.value)} required />
        <button type="submit">Submit</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default App;
