// import CanvasDraw from "react-canvas-draw";

const DrawingBoard = () => {
  const [brushColor, setBrushColor] = useState("#FF0000");
  const [brushRadius, setBrushRadius] = useState(5);

  return (
    <div>
      <h1>Facebook Emoji Drawing App</h1>
      <div
        style={{ display: "flex", justifyContent: "center", margin: "10px" }}
      >
        <button
          onClick={() => setBrushColor("#FF0000")}
          style={{
            backgroundColor: "#FF0000",
            width: "30px",
            height: "30px",
            marginRight: "10px",
          }}
        ></button>
        <button
          onClick={() => setBrushColor("#8A2BE2")}
          style={{
            backgroundColor: "#8A2BE2",
            width: "30px",
            height: "30px",
            marginRight: "10px",
          }}
        ></button>
        <button
          onClick={() => setBrushColor("#FFB6C1")}
          style={{
            backgroundColor: "#FFB6C1",
            width: "30px",
            height: "30px",
            marginRight: "10px",
          }}
        ></button>
      </div>
      <div
        style={{ display: "flex", justifyContent: "center", margin: "10px" }}
      >
        <button
          onClick={() => setBrushRadius(5)}
          style={{
            backgroundColor: "#000",
            width: "30px",
            height: "30px",
            marginRight: "10px",
          }}
        ></button>
        <button
          onClick={() => setBrushRadius(10)}
          style={{
            backgroundColor: "#000",
            width: "40px",
            height: "40px",
            marginRight: "10px",
          }}
        ></button>
        <button
          onClick={() => setBrushRadius(15)}
          style={{
            backgroundColor: "#000",
            width: "50px",
            height: "50px",
            marginRight: "10px",
          }}
        ></button>
      </div>
    </div>
  );
};

function App() {
  return (
    <div className="App">
      <DrawingBoard />
    </div>
  );
}

export default App;
