import React from 'react';
import Product from './Product';
import products from './products';
import './App.css';
function ColorBox() {
const [color, setColor] = useState("");
const styles = {
background: color,
};
return (
<div>
<h1>Color Box</h1>
<input
type="text"
style={styles}
placeholder="Type a color"
onChange={(event) => setColor(event.target.value)}
/>
</div>
);
}
export default ColorBox;
