function Product({ name, pic, price, sale, button , rating}) {
  // const stars = Array.from({length: 5}, (_, index) =>(
  //   < FaStar key = {index} color = {rating > index ? '#ffc107' : '#e4e5e9'} />
  //   ));
  return (
  <section>
  <img className="user-profile-pic" src={pic} alt={name} />
  <h2 className="user-name">
  <span className="user-first-name">{name}</span> 
  </h2>
  <p className="original-price">{price}</p>
  <p className="sale-price">{sale}</p>
  <p className="rating-r">{rating}</p>
  <button>{button}</button>
  </section>
  );
}

function App() {
  const stars = Array.from({length: 5}, (_, index) =>(
    < FaStar key = {index} color = {rating > index ? '#ffc107' : '#e4e5e9'} />
    ));
  const products = [
  {
  name: "Product 1",
  pic: "https://picsum.photos/id/1/200/300",
  price: "$19.99",
  sale: "$17.99",
  rating: stars,
  button: "addtocart",
  },
  {
  name: "Product 2",
  pic: "https://picsum.photos/id/2/200/300",
  price: "$29.99",
  rating: 1,
  button: "View Option",
  },
  ];
  return (
  <div className="App">
    <h1>Our Products</h1>
    <p>Sale</p>
  {products.map((prod) => (
  <Product name={prod.name} pic={prod.pic} price={prod.price} sale={prod.sale} button={prod.button} rating={prod.rating} />
  ))}
  </div>
  );
}
export default App;
